import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { User } from '../user.model';
import { RegistrationService } from '../registration.service';


@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})

export class RegistrationComponent {
[x: string]: any;
  signupForm: FormGroup;


  constructor(private formBuilder: FormBuilder,private registrationService: RegistrationService) {
    this.signupForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      designation: ['', Validators.required]
    });

  }


  get email() {
    return this.signupForm.get('email');
  }

  get password() {
    return this.signupForm.get('password');
  }

  get designation() {
    return this.signupForm.get('designation');
  }

  signup(): void {
    if (this.signupForm.valid) {
      // Perform the signup logic here
      console.log('Registration successful!');

    } 
    else {
      console.log('Invalid form');
    }
  }
}
<head>
  <link rel="stylesheet" href="registration.component.css">
</head>
<div class="container">
  <div class="img">
    <img src="assets\logo.png"  alt="Logo"/>
  </div>
  <h1>Create Your Account</h1>
  <form (ngSubmit)="signup()" #signupForm="ngForm">
  <div class="form-group">
    <div class="input-container">
      <label for="email">Email</label>
      <input type="email" id="email" name="email" placeholder="Enter your email" required ngModel="user.email" #emailInput="ngModel" >
      <div *ngIf="emailInput.invalid && (emailInput.dirty || emailInput.touched)" class="error-message">
        <p *ngIf="emailInput.errors?.['required']">Email is required</p>
        <p *ngIf="emailInput.errors?.['email']">Invalid email</p>
      </div>
    </div>
  </div>
    <div class="input-container">
      <label for="password">Password</label>
      <input type="password" id="password" name="password" placeholder="Enter your password" required ngModel="user.password" #passwordInput="ngModel">
      <div *ngIf="passwordInput.invalid && (passwordInput.dirty || passwordInput.touched)" class="error-message">
        <p *ngIf="passwordInput.errors?.['required']">Password is required</p>
      </div>
    </div>


    <div class="input-container">
      <label for="designation">Designation</label>
      <select id="designation" name="designation" required ngModel="user.designation" #designationInput="ngModel">
        <option value="" disabled>Select a designation</option>
        <option value="Manager">Manager</option>
        <option value="Engineer">Engineer</option>
        <option value="Analyst">Analyst</option>
      </select>
      <div *ngIf="designationInput.invalid && (designationInput.dirty || designationInput.touched)" class="error-message">
        <p *ngIf="designationInput.errors?.['required']">Designation is required</p>
      </div>
    </div>

    <div class="input-container">
      <button type="submit" class="signup-button" [disabled]="signupForm.invalid">Continue</button>
    </div>
    <div class="text-center">
    <div class="input-container">
      <p class="signup-text">Already have an account? <a href="login.component.html" class="login-link">Log In</a></p>
    </div>
    <div class="input-container">
      <p class="terms-text"><a href="#">Terms of Use</a> | <a href="#">Privacy Policy</a>.</p>
    </div>
  </div>
  </form>
</div>
