import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})

export class RegistrationComponent {
  signupForm: FormGroup;


  constructor(private formBuilder: FormBuilder) {
    this.signupForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      designation: ['', Validators.required]
    });
  }

  get email() {
    return this.signupForm.get('email');
  }

  get password() {
    return this.signupForm.get('password');
  }

  get designation() {
    return this.signupForm.get('designation');
  }

  signup() {
    if (this.signupForm.valid) {
      // Perform the signup logic here
      // console.log('Registration successful!');
       console.log('Email:', this.signupForm.value);
      // console.log('Password:', this.password.value);
      // console.log('Designation:', this.designation.value);
    } else {
      console.log('Invalid form');
    }
  }
}
<head>
  <link rel="stylesheet" href="registration.component.css">
</head>
<div class="container">
  <div class="img">
    <img src="assets\logo.png"  alt="Logo"/>
  </div>
  <h1>Create Your Account</h1>
  <form ngSubmit="signup()" #signupForm="ngForm">
  <div class="form-group">
    <div class="input-container">
      <label for="email">Email</label>
      <input type="email" id="email" name="email" placeholder="Enter your email" required ngModel="email"  #emailInput="ngModel" >
      <div *ngIf="emailInput.invalid && (emailInput.dirty || emailInput.touched)" class="error-message">
        <p *ngIf="emailInput.errors?.['required']">Email is required</p>
        <p *ngIf="emailInput.errors?.['email']">Invalid email</p>
      </div>
    </div>
  </div>
    <div class="input-container">
      <label for="password">Password</label>
      <input type="password" id="password" name="password" placeholder="Enter your password" required ngModel="password" #passwordInput="ngModel">
      <div *ngIf="passwordInput.invalid && (passwordInput.dirty || passwordInput.touched)" class="error-message">
        <p *ngIf="passwordInput.errors?.['required']">Password is required</p>
      </div>
    </div>
    <div class="input-container">
      <label for="designation">Designation</label>
      <input type="text" id="designation" name="designation" placeholder="Enter your designation" required ngModel="designation" #designationInput="ngModel">
      <div *ngIf="designationInput.invalid && (designationInput.dirty || designationInput.touched)" class="error-message">
      </div>
    </div> 
    <div class="input-container">
      <label for="designation">Designation</label>
      <select id="designation" name="designation" ngModel="designation" placeholder="Select your designation" required #designationInput="ngModel">
        <option value="" disabled>Select your designation</option>
        <option value="manager">Manager</option>
        <option value="engineer">Lawyer</option>
        <option value="analyst">Professor</option>
        <!-- Add more options as needed -->
      </select>
      <div *ngIf="designationInput.invalid && (designationInput.dirty || designationInput.touched)" class="error-message">
        <p *ngIf="designationInput.errors?.['required']">Designation is required</p>
      </div>
    </div>

    <div class="input-container">
      <button type="submit" class="signup-button" [disabled]="signupForm.invalid">Continue</button>
    </div>
    <div class="input-container">
      <p class="signup-text">Already have an account? <a href="registration.component.html" class="login-link">Log In</a></p>
    </div>
    <div class="input-container">
      <p class="terms-text"><a href="#">Terms of Use</a> | <a href="#">Privacy Policy</a>.</p>
    </div>
  </form>
</div>
