<div class="container">
  <div class="img">
    <img src="assets/logo.png" alt="Logo"/>
  </div>
  <h1>Create Your Account</h1>
  <form (ngSubmit)="signup()" [formGroup]="signupForm">
    <div class="form-group">
      <div class="input-container">
        <label for="email">Email</label>
        <input type="email" id="email" formControlName="email" placeholder="Enter your email">
        <div *ngIf="signupForm.get('email').invalid && (signupForm.get('email').dirty || signupForm.get('email').touched)" class="error-message">
          <p *ngIf="signupForm.get('email').errors?.['required']">Email is required</p>
          <p *ngIf="signupForm.get('email').errors?.['email']">Invalid email</p>
        </div>
      </div>
    </div>
    <div class="input-container">
      <label for="password">Password</label>
      <input type="password" id="password" formControlName="password" placeholder="Enter your password">
      <div *ngIf="signupForm.get('password').invalid && (signupForm.get('password').dirty || signupForm.get('password').touched)" class="error-message">
        <p *ngIf="signupForm.get('password').errors?.['required']">Password is required</p>
      </div>
    </div>
    <div class="input-container">
      <label for="designation">Designation</label>
      <select id="designation" formControlName="designation">
        <option value="" disabled selected>Select a designation</option>
        <option value="Manager">Manager</option>
        <option value="Engineer">Engineer</option>
        <option value="Analyst">Analyst</option>
      </select>
      <div *ngIf="signupForm.get('designation').invalid && (signupForm.get('designation').dirty || signupForm.get('designation').touched)" class="error-message">
        <p *ngIf="signupForm.get('designation').errors?.['required']">Designation is required</p>
      </div>
    </div>
    <div class="input-container">
      <button type="submit" class="signup-button">Continue</button>
    </div>
    <div class="text-center">
      <div class="input-container">
        <p class="signup-text">Already have an account? <a href="login.component.html" class="login-link">Log In</a></p>
      </div>
      <div class="input-container">
        <p class="terms-text"><a href="#">Terms of Use</a> | <a href="#">Privacy Policy</a>.</p>
      </div>
    </div>
  </form>
</div>
